value.css:
header {
    color: rgba(255, 255, 255);
  	padding-bottom: 3rem;
  	padding-top: 1rem;
  	background-image: linear-gradient(to right, #6b7280, #9ca3af);
  div {
    max-width: 1536px;
		width: 100%;
    margin-right: auto;
    margin-left: auto;
    padding-right: 2rem;
    padding-left: 2rem;

    nav {
      padding-bottom: 2rem;
      div {
        display: flex;
        justify-content: space-between;
      }
    }
  }
  a {
    transition-property: background-color, border-color, color, fill, stroke;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    font-size: 1.5rem;
    line-height: 2rem;
    border-bottom-width: 2px;
    font-weight: 700;
  }
  a:hover {
    transition-duration: 200ms;
  }
}

section {
  	min-width: auto;
    max-width: auto;
  	width: 100%;
    margin-right: auto;
    margin-left: auto;
    padding-right: 2rem;
  	padding-left: 2rem;
    padding-top: 3rem;
    padding-bottom: 3rem;

  .content {
    display: flex;
    flex-direction: row;
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1);
    overflow: hidden;
    background-color: rgba(255, 255, 255);

    img {
    display: block;
  	vertical-align: middle;
  	width: 33.333333%;
  	height: auto;
  	object-position: center;
  	object-fit: cover;
  	max-width: 100%;
  	border-style: solid;
  }
    .heads {
      flex-direction: column;
      display: flex;
      color: rgba(31, 41, 55);
      padding-left: 2rem;
      padding-right: 2rem;
      padding-top: 1.5rem;
      padding-bottom: 1.5rem;
      margin-left: 0.5rem;
      justify-content: center;

      h3 {
        font-weight: 600;
      	font-size: 1.5rem;
        line-height: 2rem;
      }
      p {
        color: rgba(75, 85, 99);
      }
      .body {
        padding-top: 1rem;
        margin-top: 0.5rem;
        border-top-width: 1px;
        border-color: rgba(229, 231, 235);
      }
      ul {
        display: flex;
        margin-top: 1.5rem;
        margin-bottom: 1.5rem;

        li {
           color: rgba(255, 255, 255);
           padding-left: 1.25rem;
           padding-right: 1.25rem;
           padding-top: 0.75rem;
           padding-bottom: 0.75rem;
           margin-right: 0.5rem;
           font-weight: 500;
           border-radius: 0.25rem;
           background-color: rgba(99, 102, 241);
        }
        li:hover {
          background-color: rgba(79, 70, 229);
        }
      }

    }

  }
}

@media (max-width: 600px) {
  section {
    .content {
      flex-direction: column;

      img {
        width: 100%;
      }
    }
  }
}

section {
  max-width: 1536px;
	display: flex;
  flex-direction: column;
	width: 100%;
  margin: 0 auto;
  padding: 3rem 2rem;

  .top-title {
    display: flex;
    flex: 1 1 0%;
    padding-top: 0.5rem;
    padding-bottom:0.5rem;
    font-weight: 600;
    transition-property: background-color, border-color, color, fill, stroke;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    transition-duration: 200ms;
    color: rgba(243, 244, 246);

    button {
      padding-top: 0.5rem;
      padding-bottom: 0.5rem;
      transition-property: background-color, border-color, color, fill, stroke;
      transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
      transition-duration: 200ms;
      font-weight: 600;
      flex: 1 1 0%;
      border-right-width: 1px;
      border-color: rgba(99, 102, 241);
      background: rgba(79, 70, 229);

      &.active, &:hover, &:hover {
  			background: rgba(67, 56, 202);
      }
    }

    i {
    	margin-right: 0.25rem;
    }
    span {
      display: inline;
    }
  }
  .content {
    padding-left: 2rem;
    padding-right: 2rem;
    padding-top: 1.5rem;
    padding-bottom: 1.5rem;
    background-color: rgba(255, 255, 255);
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
  }
}

section {
  display: block;
  margin-right: auto;
  margin-left: auto;
  padding: 3rem 2rem;

  figure {
      display: grid;
      grid-template-columns: repeat(2, minmax(0, 1fr));
      gap: 1rem;

    a {
      height: 16rem;
      position: relative;
      overflow: hidden;
      border-radius: 0.75rem;
      background-color: transparent;
      box-shadow: var(--tw-ring-offset-shadow, 0 0 #0000), var(--tw-ring-shadow, 0 0 #0000), var(--tw-shadow);

    }
    img {
      position: absolute;
      left: 0;
      right: 0;
      top: 0;
      object-fit: cover;
      height: 100%;
      width: 100%;
      transition-property: opacity;
      transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
      transition-duration: 150ms;
    }
  p {
      display: flex;
      position: relative;
      z-index: 0;
      width: 100%;
      transition-duration: 200ms;
      color: rgba(255, 255, 255);
      font-size: 1.5rem;
      line-height: 2rem;
      height: 100%;
      font-weight: 700;
      justify-content: center;
      align-items: center;
      background-color: black;
      opacity: 0;
      transition-property: opacity;
      transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
      transition-duration: 150ms;

  }
    p:hover {
      opacity: 0.7;
      color: white;
    }
  }
}

@media (max-width: 600px) {
  section {
    figure {
      grid-template-columns: 1fr;
    }
  }
}
