import AlpineJS from 'alpinejs'

  window.loadCards = () => {
    return {
      search: "",
      cards: primo.data.employees,
      get filteredCards() {
        if (this.search === "") {
          return this.cards;
        }
        const filteredCards = this.cards.filter((item) => {
          const content = `${item.title} ${item.subtitle}`
          return content
            .toLowerCase()
            .includes(this.search.toLowerCase());
        }) 
        const withHighlight = filteredCards.map(card => ({
        	...card,
            highlightTitle: card.title.replace(this.search, `<mark>${this.search}</mark>`),
            highlightSubtitle: card.subtitle.replace(this.search, `<mark>${this.search}</mark>`)
        }))
        return withHighlight
      },
    };
  }

