value.css:
section {
  max-width: 1536px;
	display: flex;
  flex-direction: column;
	width: 100%;
  margin-right: auto;
  margin-left: auto;
  padding-right: 2rem;
	padding-left: 2rem;
  padding-top: 3rem;
  padding-bottom: 3rem;

  .top-title {
    display: flex;
    flex: 1 1 0%;
    padding-top: 0.5rem;
    padding-bottom:0.5rem;
    font-weight: 600;
    border-color: rgba(99, 102, 241);
    border-right-width: 1px;
    background-color: rgba(79, 70, 229);
    transition-property: background-color, border-color, color, fill, stroke;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    transition-duration: 200ms;
    color: rgba(243, 244, 246);
  }
}

/* a:hover p, a:focus p {
	@apply opacity-100;
} */

@media (min-width: 1536px) { 
  section {
  	display: block;
    padding-top: 3rem;
    padding-bottom: 3rem;
    margin-right: auto;
    margin-left: auto;
    padding-right: 2rem;
    padding-left: 2rem;

  figure {
    	display: grid;
			grid-template-columns: repeat(2, minmax(0, 1fr));
    	gap: 1rem;

    a {
      height: 16rem;
      position: relative;
      overflow: hidden;
      border-radius: 0.75rem;
      background-color: transparent;
      --tw-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04);
			box-shadow: var(--tw-ring-offset-shadow, 0 0 #0000), var(--tw-ring-shadow, 0 0 #0000), var(--tw-shadow);

    }
    img {
      position: absolute;
    	left: 0;
    	right: 0;
    	top: 0;
    	object-fit: cover;
    	height: 100%;
      width: 100%;
			transition-property: opacity;
    	transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    	transition-duration: 150ms;
    }
  p {
    	display: flex;
    	position: relative;
    	z-index: 0;
  		width: 100%;
    	transition-duration: 200ms;
    	color: rgba(255, 255, 255);
    	font-size: 1.5rem;
  		line-height: 2rem;
    	height: 100%;
    	font-weight: 700;
      justify-content: center;
			align-items: center;
		  background-color: black;
    	opacity: 0;
    	transition-property: opacity;
			transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
			transition-duration: 150ms;

  }
    p:hover {
      opacity: 0.7;
      color: white;
    }
  }
}

}
